document.addEventListener("DOMContentLoaded",function(){var toastEl=document.getElementById('liveToast');if(toastEl){var toast=new bootstrap.Toast(toastEl);toast.show();}});;const togglePassword=document.querySelector('#togglePassword');const password=document.querySelector('#password');togglePassword.addEventListener('click',function(){const type=password.getAttribute('type')==='password'?'text':'password';password.setAttribute('type',type);this.querySelector('i').classList.toggle('bi-eye');this.querySelector('i').classList.toggle('bi-eye-slash');});;