const togglePassword=document.querySelector('#togglePassword');const toggleConfirmPassword=document.querySelector('#toggleConfirmPassword');const password=document.querySelector('#password');const confirmPassword=document.querySelector('#confirmPassword');togglePassword.addEventListener('click',function(){const type=password.getAttribute('type')==='password'?'text':'password';password.setAttribute('type',type);this.querySelector('i').classList.toggle('bi-eye');this.querySelector('i').classList.toggle('bi-eye-slash');});toggleConfirmPassword.addEventListener('click',function(){const type=confirmPassword.getAttribute('type')==='password'?'text':'password';confirmPassword.setAttribute('type',type);this.querySelector('i').classList.toggle('bi-eye');this.querySelector('i').classList.toggle('bi-eye-slash');});document.querySelector('form').addEventListener('submit',function(e){e.preventDefault();const passwordValue=password.value;const confirmPasswordValue=confirmPassword.value;if(passwordValue!==confirmPasswordValue){alert('As senhas não coincidem! Por favor, verifique novamente.');return;}
if(!document.getElementById('terms').checked){alert('Você deve aceitar os Termos de Uso e Política de Privacidade para continuar.');return;}
alert('Cadastro realizado com sucesso! Bem-vindo à nossa plataforma.');this.reset();});;